import ScrollAnim from 'rc-scroll-anim';

import ReactDOM from 'react-dom';
// var Scroll = require('react-scroll');
import React from 'react';
// import Button from '@material-ui/core/Button'; import Dialog from
// '@material-ui/core/Dialog'; import IconButton from
// '@material-ui/core/IconButton'; import CloseIcon from
// '@material-ui/icons/Close';
import TweenOne from 'rc-tween-one';
import Tween from 'rc-tween-one';
import content from './SectionsContent';
import Intro from './Intro';
import zIndex from '@material-ui/core/styles/zIndex';
const ScrollOverPack = ScrollAnim.OverPack;
const Link = ScrollAnim.Link;
const Element = ScrollAnim.Element;
const EventListener = ScrollAnim.Event;

ScrollAnim
    .scrollScreen
    .init({loop: false, duration: 1200, ease: "easeInOutExpo", scrollInterval: 1400});

const find = id => content.find(p => p.id === id);

function Sections() {
    return (<Home/>)
}
class Conteudo extends React.Component {
    constructor({data, props}) {
        super({data, props});
        this.state = {
            show: true,
            reverse: false,
            reverseDelay: 1000,
            tweenData: [
                {
                    delay: 0,
                    duration: 400,
                    opacity: 1,
                    x: 0,
                    y: -5,
                    top: 0,
                    ease: "easeInOutSine"
                }
            ]
        };
        // var data = props;
        this.n = find(data.params.id)
    }
    onReverse = () => {
        this.setState({reverse: true, reverseDelay: 2000, show: false});
    }

    // componentWillReceiveProps(nextProps){                this.setState({ show:
    // false, reverse:true }); } componentDidMount(){     setTimeout(() => {
    //        this.setState({ show: true, reverse: false });             },1000); }
    componentDidMount() {
        // this.setState({ show: true, reverse: true });
        setTimeout(() => {
            this.setState({
                reverse: false,
                show: true,
                style: {
                    opacity: 0.5,
                    transform: 'translateX(0px) translateY(0px)',
                    backgroundColor: this.n.bg,
                    top: '0'
                }
            });

        }, 2000);
    }

    handleScroll() {
        this.setState({reverse: true, show: false, reverseDelay: 100});

        console.log('scroll')
    }
    // onStart(){     setTimeout(() => {         this.setState({reverse: true});
    // }, 10); } componentDidMount() {     setTimeout(() => {        this.setState({
    // show: true, reverse: false });     }, 10);   }
    render() {
        return (
            <ScrollOverPack
                className={this.n.classe + this.n.id}
                id={this.n.hash + this.n.id}
                style={{
                backgroundColor: this.n.bg
            }}
                playScale={0.1}>
                <Tween
                    component={'section'}
                    show={this.state.show}
                    animation={this.state.tweenData}
                    style={this.state.style}
                    reverse={this.state.reverse}
                    reverseDelay={this.state.reverseDelay}
                    className={"tween-one cabecalho"}
                    key={this.n.id}>
                    <h1 style={{
                        color: this.n.cor
                    }}>{this.n.letra}</h1>
                    <label>{this.n.label}</label>
                    <h2>{this.n.h2}</h2>
                    <div className='descricao'>{this.n.descricao}</div>
                </Tween>
            </ScrollOverPack>
        )
    }
}

class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            paused: true,
            // show: false,
            open: false,
            // reverse: false,
            buttonEnterReverse: false,
            buttonCloseReverse: false
        };
    }
    onPlay = () => {
        this.setState({paused: false, reverse: false, buttonEnterReverse: true, buttonCloseReverse: false});
        setTimeout(() => {
            this.handleClickOpen()
            this.setState({open: true});
        }, 3400);
    }

    handleClickOpen = () => {
        this.setState({
            items: [
                {
                    key: 1
                }, {
                    key: 2
                }, {
                    key: 3
                }, {
                    key: 4
                }, {
                    key: 5
                }, {
                    key: 6
                }
            ]
        })
        setTimeout(() => {
            ScrollAnim
                .scrollScreen
                .unMount();
        }, 1000);
    };

    // componentDidMount() {     window.addEventListener('resize',
    // this.handleWindowSizeChange.bind(this));
    // window.removeEventListener('resize', this.handleWindowSizeChange.bind(this));
    // }
    handleWindowSizeChange = () => {
        ScrollAnim
            .scrollScreen
            .init({loop: false, duration: 1200, ease: "easeInOutQuart", scrollInterval: 800});
    };
    handleClose = () => {
        this.setState({items: []});
        this.setState({paused: false, reverse: true, buttonCloseReverse: true, show: true});
        setTimeout(() => {
            this.setState({buttonEnterReverse: false, open: false, show: false});
            ScrollAnim
                .scrollScreen
                .init({loop: false, duration: 1200, ease: "easeInOutQuart", scrollInterval: 800});
        }, 3200);
    };
    render() {
        return (
            <span>
                <Intro/>
                <Conteudo
                    data={{
                    params: {
                        id: 1
                    }
                }}/>
                <Conteudo
                    data={{
                    params: {
                        id: 2
                    }
                }}/><Conteudo
                    data={{
                params: {
                    id: 3
                }
            }}/><Conteudo
                    data={{
                params: {
                    id: 4
                }
            }}/>
                <div
                    style={{
                    width: '100%',
                    height: '100vh',
                    position: 'fixed',
                    backgroundColor: '#f0f0f0',
                    zIndex: 6,
                    top: 0
                }}></div>
            </span>
        );
    }
}

export default Sections;
