import ScrollAnim from 'rc-scroll-anim';
import React from 'react';
import ReactDOM from 'react-dom';
// import { HashRouter as Router, Route, NavLink } from "react-router-dom";
// import Home from './Home';

const Link = ScrollAnim.Link;
const EventListener = ScrollAnim.Event;
// import Ui from './Ui';


class Menu extends React.Component {
  constructor() {
    super();
    this.state = {
      show: false,
    }
    // const classe = this.dom.className;
  }

  onFocus = (e) => {
    this.dom = e.target;
    // to.className = "active";

    this.barAnimate();
    // var idOn = document.getElementById(e.to)
    // if(e.target.index)

        // idOn.className =  "pack-page " + e.to + " active"

  //  if(e.target.className(active) { this.setState({ selected: true }); console.log(idOn.className);}
    // this.state.selected


    // this.state.selected ? 'click-state' : 'base-state';
  }
  render() {


      const count = [1, 2, 3, 4, 5];
      const linkItems = count.map((number) =>
        <Link className={'nav-link'} to={'page'+ number} showHeightActive={['50%', '10%']}

        onFocus={this.onFocus}
        onBlur={this.onBlur}
        ref={ (c) => {this.dom = ReactDOM.findDOMNode(c);}}
    >Example{number}</Link>

      );
    return (
      <div className="nav-wap">
        {linkItems}
        </div>
    );
  }
}





export default Menu;
