    //     setTimeout(() => {
    //         this.handleClickOpen()
    //         this.setState({open: true});
    //     }, 3400);
    // }

    // handleClickOpen = () => {
    //     this.setState({
    //         items: [
    //             {
    //                 key: 1
    //             }, {
    //                 key: 2
    //             }, {
    //                 key: 3
    //             }, {
    //                 key: 4
    //             }, {
    //                 key: 5
    //             }, {
    //                 key: 6
    //             }
    //         ]
    //     })
    //     setTimeout(() => {
    //         ScrollAnim
    //             .scrollScreen
    //             .unMount();
    //     }, 1000);
    // };

    // // componentDidMount() {     window.addEventListener('resize',
    // // this.handleWindowSizeChange.bind(this));
    // // window.removeEventListener('resize', this.handleWindowSizeChange.bind(this));
    // // }
    // handleWindowSizeChange = () => {
    //     ScrollAnim
    //         .scrollScreen
    //         .init({loop: false, duration: 1200, ease: "easeInOutQuart", scrollInterval: 800});
    // };
    // handleClose = () => {
    //     this.setState({items: []});
    //     this.setState({paused: false, reverse: true, buttonCloseReverse: true, show: true});
    //     setTimeout(() => {
    //         this.setState({buttonEnterReverse: false, open: false, show: false});
    //         ScrollAnim
    //             .scrollScreen
    //             .init({loop: false, duration: 1200, ease: "easeInOutQuart", scrollInterval: 800});
    //     }, 3200);
    // };
    render() {
        return (
            <span>
                <Intro/>
                <Conteudo
                    data={{
                    params: {
                        id: 1
                    }
                }}/>
                <Conteudo
                    data={{
                    params: {
                        id: 2
                    }
                }}/><Conteudo
                    data={{
                params: {
                    id: 3
                }
            }}/><Conteudo
                    data={{
                params: {
                    id: 4
                }
            }}/>

            </span>
        );
    }
}

export default Sections;
