  class Home extends React.Component {
    constructor(props) {
            super(props);
            this.state = {
              modalActive: false
            };

            this.activateModal = this.activateModal.bind(this);
            this.deactivateModal = this.deactivateModal.bind(this);
            this.getApplicationNode = this.getApplicationNode.bind(this);
            this.activateInnerFocusTrap = this.activateInnerFocusTrap.bind(this);
            this.deactivateInnerFocusTrap = this.deactivateInnerFocusTrap.bind(this);
          }

          activateModal() {
            this.setState({ modalActive: true });

          }

          deactivateModal() {
            this.setState({ modalActive: false });
          }

          activateInnerFocusTrap() {
                    this.setState({ innerFocusTrapActive: true });
                  }

                  deactivateInnerFocusTrap() {
                    this.setState({ innerFocusTrapActive: false });
                  }

                  getApplicationNode() {
                    return document.getElementById('application');
                  }
          // find();



        render() {
          const modal = this.state.modalActive
      ?
      <AriaModal
      titleText="demo three"
      focusDialog={true}
      escapeExits={false}
      underlayStyle={{ padding: '0' }}
      aria-describedby='describer'
      data-test-id='test-id'
    >
          <Modal match={{ params: { id: 1}, titulo: 'Intro' }} />

        </AriaModal>
        : false;


        return (
          <div>
          <Link style={{position: 'fixed', 'z-index': '1050'}} onClick={this.activateModal}>aaa</Link> 
          <Link style={{ position: 'fixed', right: '0', top: '0em', 'z-index': '9999' }} onClick={this.deactivateModal}>
                Xssss
              </Link>
          <Intro match={{ params: { id: 0}, titulo: 'Intro' }} />
          <Conteudo match={{ params: { id: 1, titulo: 'Pop'} }} />
          <Conteudo match={{ params: { id: 2, titulo: 'Vivo'} }} />
          <Conteudo match={{ params: { id: 3, titulo: 'Pop'} }}  />
          <Conteudo match={{ params: { id: 4, titulo: 'Diageo'} }}   />
          {/* <Modal match={{ params: { id: 4, titulo: 'Diageo'} }} component={Modal}  /> */}

          {modal}

            </div>


        );
      }
      }


function Intro({ match }) {
    let data = find(match.params.id);
    return (

        <Element className="pack-page page0" id={data.id} 
            style={{ backgroundColor: '#ffffff' }}
          >
            <QueueAnim key='queueAnim' type={['right','alpha']} className="intro" interval={[200, 700]} delay={800}
                duration={[800, 500]}
                playScale={0.1}>
                <div key='a'>Olá!</div>
                <div key='b'>Sou Alexandre Almeida,</div>
                <div key='c'>Senior Product Designer na <a href="http://ogilvy.com.br">Ogilvy & Mather Brasil</a>,</div>
                <div key='d'></div>
                <div key='e'>São Paulo.</div>

            </QueueAnim>
        </Element>
    );
  }
  function Modal({ match }) {
    let data = find(match.params.id);
    return (

          <div id="demo-three-modal" className="modal" style={{ background: "#f0f0f0"}}>
          <div className="nav">
