import React from 'react';
import TweenOne from 'rc-tween-one';


function DialogTransition({n, key, reverse}) {

    const state = {
        reverse: false
    }
    const styles = {
        width: '100%',
        height: '100vh',
        position: 'absolute',
        zIndex: '0',
        backgroundColor: n.cor2,
        opacity: 1,
        top: 0,
        left: 0,
        borderRadius: '30%',
        transform: 'translateX(0) translateY(0) scale(1, 0.3)'
    }
    const dialAnimation = {
        from: 
            {
                delay: 90,
                        duration: 1000,
                        opacity: 0,
                        top: 0,
                        left: 0,
                        ease: "easeInOutExpo",
                        backgroundColor: n.cor2,
                        type: 'from',
                        scaleX: 0.5, scaleY: 0.5,
                        x: '0',
                        y: '0'
            }
        ,
        to: 
            {
                delay: 0,
                        duration: 1000,
                        opacity: 1,
                        top: '-10vh',
                        ease: "easeInOutExpo",
                        backgroundColor: n.cor2,
                        type: 'to',
                        scaleX: 3, scaleY: .9,
                        x: 0,
                        y: 0
            }

    };
    return (
        <TweenOne 
        key={key}
        reverse={reverse}
                animation={
                [dialAnimation.from, dialAnimation.to]
            }
                style={styles}/>
    )
}

export default DialogTransition;
