import React from 'react';
import ScrollAnim from 'rc-scroll-anim';
import content from './SectionsContent';
const Link = ScrollAnim.Link;
const EventListener = ScrollAnim.Event;

const Menu = () => {

    content.map(i => (
        <Link
            className="nav-list"
            key={i.id}
            to={i.hash}
            toShowHeight
            onFocus={this.onFocus}
            onBlur={this.onBlur}>
            Example2
        </Link>
    ))

}
function Gallery() {
    return (
      <div>
        {IMAGES.map(i => (
          <Link
            key={i.id}
            to={{
              pathname: `/img/${i.id}`,
              // this is the trick!
              state: { modal: true }
            }}
          >
            <Thumbnail color={i.color} />
            <p>{i.title}</p>
          </Link>
        ))}
      </div>
    );
  }

  function ImageView({ match }) {
    let image = IMAGES[parseInt(match.params.id, 10)];

    if (!image) return <div>Image not found</div>;

    return (
      <div>
        <h1>{image.title}</h1>
        <Image color={image.color} />
      </div>
    );
  }
class behaviorMenu extends React.Component {
    componentDidMount() { //
        EventListener.addEventListener('resize.userResize', this.barAnimate.bind(this));
        window.addEventListener('resize', this.handleWindowSizeChange.bind(this));
        window.removeEventListener('resize', this.handleWindowSizeChange.bind(this));
    }
    handleWindowSizeChange = () => {
        // setTimeout(() => {
            // iniciaScroll()             }, 50);
        };
        render() {
            return (
                <div className="nav">
                    <div className="nav-wap">
                        <Link
                            className="nav-list logo"
                            to={"page0"}
                            toShowHeight
                            onFocus={this.onFocus}
                            onBlur={this.onBlur}>
                            Alê Almeida
                        </Link>
                        <Link
                            className="nav-list"
                            to={i.hash}
                            toShowHeight
                            onFocus={this.onFocus}
                            onBlur={this.onBlur}>
                            Example2
                        </Link>
                        <div ref= {(c) => { this.bar = c; } } className="nav-bar"/>
                    </div>
                </div>
            );
        }
    }

    export default Menu;
