import React from 'react';
import {HashRouter as Router, Route, Link} from "react-router-dom";
import Dialog from '@material-ui/core/Dialog';
import CloseIcon from '@material-ui/icons/Close';
import Slide from '@material-ui/core/Slide';
import ScrollAnim from 'rc-scroll-anim';
import Button from '@material-ui/core/Button';
import content from './SectionsContent';
import CallDialog from './Dialog';

// const find = id => content.find(p => p.id === id);

class Dials extends React.Component {
    constructor({props}) {
                super({props});
                this.state = {
                    open: false
                };
            }
    handleClickOpen = () => {
            // this.setState({reverse: false});
            setTimeout(() => {
                this.setState({open: true});
            }, 100);
        };
            handleClose = () => {
                this.setState({reverse: true});
                setTimeout(() => {
                    this.setState({open: false});
                }, 1000);
            };
    render() {
        return(

               <div>
                     <Button
                        className="ver_mais"
                        onClick={this.handleClickOpen}
                        style={{
                        height: '4em',
                        left: 0,
                        top: 0,
                        zIndex: 999999,
                        cursor: 'pointer',
                        position: 'fixed'
                    }}>
                        {'ver projeto'}
                    </Button>
                    <Dialog fullScreen 
                        elevation={8} open={this.state.open} transitionDuration={{
                        enter: 0,
                        exit: 0
                    }} style={{
                        backgroundColor: '#ffffff',
                        zIndex: 99999,
                        height: '100%',
                        position: 'absolute'
                    }}>
                        <CallDialog m={{p: {id: 1}}} />
                        <Button
                            style={{
                            backgroundColor: 'transparent',
                            top: 0,
                            position: "fixed",
                            zIndex: 19,
                            right: '1em'
                        }}
                            onClick={this.handleClose}>
                            <CloseIcon
                                style={{
                                width: 'calc((100vw+100vh)/2/12)',
                                height: 'calc((100vw+100vh)/2/12)'
                            }}/>
                        </Button>
                    </Dialog>
                    </div>

        )

    }
}
export default Dials;
