import ScrollAnim from 'rc-scroll-anim';
// import ReactDOM from 'react-dom';
import React from 'react';
import PropTypes from 'prop-types';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import ListItemText from '@material-ui/core/ListItemText';
import ListItem from '@material-ui/core/ListItem';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
// import Icon from '@material-ui/core/Icon'
import Typography from '@material-ui/core/Typography';
import CloseIcon from '@material-ui/icons/Close';
import Slide from '@material-ui/core/Slide';
import Grow from '@material-ui/core/Grow';
import Popover from '@material-ui/core/Popover';

import Zoom from '@material-ui/core/Zoom';
import Fade from '@material-ui/core/Fade';
import {
    withStyles
} from '@material-ui/core/styles';
import QueueAnim from 'rc-queue-anim';
import TweenOne from 'rc-tween-one';
import content from './SectionsContent';
import {Collapse} from '@material-ui/core';
// import {
//     Contexto,
//     datas
// } from './SectionsContexto';
// import Botao from './SectionsBotoes';
const Element = ScrollAnim.Element;
const ScrollOverPack = ScrollAnim.OverPack;

function find(id) {
    return content.find(p => p.id === id);
}
const styles = {
    appBar: {
        position: 'relative'
    },
    flex: {
        flex: 1
    },
};

function Transition(props) {
    return ( <Popover 
	anchorOrigin={{
	  vertical: 'top',
	  horizontal: 'left',
	}}
	transformOrigin={{
	  vertical: 'top',
	  horizontal: 'left',
	}}
  >
	The content of the Popover.
  </Popover> )

}

function Sections() {
    return <span > < Home / > < /span>
}

class Home extends React.Component {
    state = {
		open: false,
		anchorOriginVertical: 'top',
		anchorOriginHorizontal: 'left',
		transformOriginVertical: 'top',
		transformOriginHorizontal: 'left',
		positionTop: 200, // Just so the popover can be spotted more easily
		positionLeft: 400, // Same as above
		anchorReference: 'anchorEl',
	  };
	  handleChange = key => (event, value) => {
		this.setState({
		  [key]: value,
		});
	  };

	  handleNumberInputChange = key => event => {
		this.setState({
		  [key]: parseInt(event.target.value, 10),
		});
	  };

	  handleClickButton = () => {
		this.setState({
		  open: true,
		});
	  };

    handleClose = () => {
        this.setState({
            open: false
        });
        ScrollAnim.scrollScreen.init({
            loop: false,
            duration: 1200,
            ease: "easeInOutQuart",
            scrollInterval: 800
        });
    };
    render() {
        const classes = this.props
		return ( <div>
			<Intro match={{ params: {id: 0}, titulo: 'Intro'}} />
			{content.map(i => ( 
			<ScrollOverPack id={ i.hash + i.id } className={ i.classe + i.id } style = { { backgroundColor: i.bg,zIndex: i.zindex }} playScale = {0.1}>
				<TweenOne className={ "tween-one"} key = "0" animation={{
						delay: 200,
						duration: 0,
						translateY: 0,
						opacity: 1
				}}
				style = {{
						transform: 'translateY(0px)',
						opacity: 0
				}} >
					<h2> { i.h2 } </h2>
					<div> { i.descricao } </div>
					<Button style = {{
							position: 'fixed',
							zIndex: '9999',
							top: '1em',
							right: '1em'
						}}
					onClick = {
						this.handleClickOpen
					} > Open full - screen dialog </Button>
				</TweenOne>

			<Dialog fullScreen scroll={ this.state.scroll } open={ this.state.open} onClose={ this.handleClose }
			TransitionComponent={ Transition } >
				<IconButton style={{
						position: 'fixed',
						zIndex: '9999',
						top: '1em',
						right: '1em'
				}}
				color="inherit"
				onClick = {
					this.handleClose
				} aria-label="Close" >
					<CloseIcon />
				</IconButton>
					<div className={"tween-one"}
					animation={{
						delay: 0,
						duration: 0,
						opacity: 1
					}}
						style={{ transform: 'translateY(0px)',opacity: 1 }} >
						<h2>{i.h2}</h2>
						<div>{i.descricao}</div>
					</div> 
			</Dialog>
			</ScrollOverPack>
			))} 
		</div>
    );
}
}

function Intro({ match }) {
    // let data = find(match.params.id);
    return (
		<Element className="pack-page page0" id ='page0'
        style = {
            {
                backgroundColor: '#ffffff'
            }
        } > < QueueAnim key = 'queueAnim'
        type = {
            ['right', 'alpha']
        }
        className = "intro"
        interval = {
            [200, 700]
        }
        delay = {
            800
        }
        duration = {
            [800, 500]
        }
        playScale = {
            0.1
        } > < div key = 'a' > Olá! < /div> <
        div key = 'b' > Sou Alexandre Almeida, < /div> <
        div key = 'c' > Senior Product Designer na < a href = "http://ogilvy.com.br" > Ogilvy & Mather Brasil < /a>,</div > < div key = 'd' > < /div> <
        div key = 'e' > São Paulo. < /div> < /QueueAnim > < /
        Element > );
}
function Conteudo({ match }) {
    let i = find(match.params.id);
    return (
		<ScrollOverPack id={ i.hash + i.id } className={ i.classe + i.id } style = { { backgroundColor: i.bg,zIndex: i.zindex }} playScale = {0.1}>
				<TweenOne className={ "tween-one"} key = "0" animation={{
						delay: 200,
						duration: 0,
						translateY: 0,
						opacity: 1
				}}
				style = {{
						transform: 'translateY(0px)',
						opacity: 0
				}} >
					<h2> { i.h2 } </h2>
					<div> { i.descricao } </div>
					<Button style = {{
							position: 'fixed',
							zIndex: '9999',
							top: '1em',
							right: '1em'
						}}
					onClick = {
						this.handleClickOpen
					} > Open full - screen dialog </Button>
				</TweenOne>
			</ScrollOverPack>
	);
}
Home.propTypes = {
    classes: PropTypes.object.isRequired
};
export default withStyles(styles)(Sections);
