import ScrollAnim from 'rc-scroll-anim';
import ReactDOM from 'react-dom';
import React from 'react';

import Dialog from '@material-ui/core/Dialog';


import QueueAnim from 'rc-queue-anim';
import TweenOne from 'rc-tween-one';
import AriaModal from '../../react-aria-modal';

import content from './SectionsContent';
import {
	Contexto,
	datas
} from './SectionsContexto';
import Botao from './SectionsBotoes';




const Element = ScrollAnim.Element;
const ScrollOverPack = ScrollAnim.OverPack;

function find(id) {
	return content.find(p => p.id === id);
}

const styles = {
	appBar: {
		position: 'relative',
	},
	flex: {
		flex: 1,
	},
};

function Transition(props) {
	return <Slide direction = "up" { ...props
	}
	/>;
}

function Sections() {
	return ( <
		span >
		<
		Home / >
		<
		/span>

	)
}

function Toolbar(props) {
	return ( <
		Botao onClick = {
			props.changeContexto
		} >
		Change Theme <
		/Botao>
	);
}
class Home extends React.Component {


	constructor(props) {
		super(props);

		this.state = {
			modalActive: false,
			open: false,
		};
		handleClickOpen = () => {
			this.setState({
				open: true
			});
		};

		handleClose = () => {
			this.setState({
				open: false
			});
		};
		this.activateModal = this.activateModal.bind(this);
		this.deactivateModal = this.deactivateModal.bind(this);
		this.getApplicationNode = this.getApplicationNode.bind(this);
	}

	activateModal = () => {
		this.setState({
			modalActive: true
		});
	};

	deactivateModal = () => {
		this.setState({
			modalActive: false
		});
	};

	getApplicationNode() {
		return document.getElementById('application');
	}

	render() {

			return ( <
				div >



				<
				Intro match = {
					{
						params: {
							id: 0
						},
						titulo: 'Intro'
					}
				}
				/> {
				content.map(i => (


						<
						ScrollOverPack id = {
							i.hash + i.id
						}
						className = {
							i.classe + i.id
						}
						style = {
							{
								backgroundColor: i.bg,
								'z-index': i.zindex
							}
						}
						playScale = {
							0.1
						} >
						<
						TweenOne className = {
							"tween-one conteudo-colapsado"
						}
						key = "0"
						animation = {
							{
								delay: 200,
								duration: 0,
								translateY: 0,
								opacity: 1
							}
						}
						style = {
							{
								transform: 'translateY(0px)',
								opacity: 0
							}
						} >
						<
						h2 > {
							i.h2
						} < /h2> <
						div > {
							i.descricao
						} < /div>



						<
						AriaModal titleText = "demo four"
						onExit = {
							this.deactivateModal
						}
						initialFocus = "#demo-four-deactivate"
						mounted = {
							this.state.modalActive
						}
						underlayStyle = {
							{
								paddingTop: '0'
							}
						} >


						<
						div id = "demo-four-modal"
						className = "modal"
						style = {
							{
								background: '#dddddd'
							}
						} >
						<
						div className = "modal-body" >
						<
						button id = "demo-four-deactivate"
						className = "ver_mais"
						style = {
							{
								position: 'fixed',
								top: '2em',
								right: '1em',
								background: 'transparent',
								border: 0
							}
						}
						onClick = {
							this.deactivateModal
						} >
						fechar <
						/button> <
						div className = {
							i.classe + i.id
						} >
						<
						div className = "tween-one" >
						<
						h2 > {
							i.h2
						} < /h2> <
						div > {
							i.descricao
						} < /div> < /
						div > <
						/div> <
						div className = "pack-page"
						style = {
							{
								backgroundColor: '#aaa',
								position: 'relative',
								textAlign: 'center',
								fontSize: 36,
							}
						} >

						<
						/ div >

						<
						div > {
							i.descricao
						} < /div>



						<
						div > {
							i.descricao
						} < /div>



						<
						/div> < /
						div > <
						/AriaModal>

						<
						button className = "ver_mais"
						style = {
							{
								position: 'relative',
								'z-index': '1050',
								top: '2em',
								background: 'transparent',
								border: 0
							}
						}
						onClick = {
							this.activateModal
						} >
						ver projeto <
						/button> {
						/* <button style={{position: 'fixed', 'z-index': '1050', top: '2em', left: '1em' }} onClick={this.activateModal}>
												activate modal
											</button>
																<Link  style={{position: 'fixed', 'z-index': '1050', top: '2em', left: '1em' }} onClick={this.activateModal.bind(this)} >aaa</Link>         */
					} <
					/TweenOne>

					<
					/ScrollOverPack>






				))
		}

		<
		/div>



);
}
}


function Intro({
	match
}) {
	let data = find(match.params.id);
	return (

		<
		Element className = "pack-page page0"
		id = 'page0'
		style = {
			{
				backgroundColor: '#ffffff'
			}
		} >
		<
		QueueAnim key = 'queueAnim'
		type = {
			['right', 'alpha']
		}
		className = "intro"
		interval = {
			[200, 700]
		}
		delay = {
			800
		}
		duration = {
			[800, 500]
		}
		playScale = {
			0.1
		} >
		<
		div key = 'a' > Olá! < /div> <
		div key = 'b' > Sou Alexandre Almeida, < /div> <
		div key = 'c' > Senior Product Designer na < a href = "http://ogilvy.com.br" > Ogilvy & Mather Brasil < /a>,</div >
		<
		div key = 'd' > < /div> <
		div key = 'e' > São Paulo. < /div>

		<
		/QueueAnim> < /
		Element >
	);
}

function Conteudo({
	match
}) {
	let data = find(match.params.id);


	return (

		<
		ScrollOverPack id = {
			data.hash + data.id
		}
		className = {
			data.classe + data.id
		}
		style = {
			{
				backgroundColor: data.bg,
				'z-index': data.zindex
			}
		}
		playScale = {
			0.1
		} >
		<
		TweenOne className = {
			"tween-one conteudo-colapsado"
		}
		key = "0"
		animation = {
			{
				delay: 200,
				duration: 0,
				translateY: 0,
				opacity: 1
			}
		}
		style = {
			{
				transform: 'translateY(0px)',
				opacity: 0
			}
		} >
		<
		h2 > {
			data.h2
		} < /h2> <
		div > {
			data.descricao
		} < /div>


		<
		/TweenOne> < /
		ScrollOverPack >


	);
}

export default Sections;
