//ScrollAnim.scrollScreen.unMount({ });


const Link = ScrollAnim.Link;
const Element = ScrollAnim.Element;
const ScrollOverPack = ScrollAnim.OverPack;
const EventListener = ScrollAnim.Event;


function find(id) {
  return content.find(p => p.id ===id);
}
function Sections() {
    console.log(content.titulo);
    return (
      <span>
      <Intro match={{ params: { id: 0}, titulo: 'Intro' }}  />
      <Conteudo match={{ params: { id: 1, titulo: 'Pop'} }} component={chamaModal}  />
      <Conteudo match={{ params: { id: 2, titulo: 'Vivo'} }}  component={chamaModal} />
      <Conteudo match={{ params: { id: 3, titulo: 'Pop'} }}  component={chamaModal} />
      <Conteudo match={{ params: { id: 4, titulo: 'Diageo'} }}  component={chamaModal}  />

      </span>
    )
  }
  function Intro({ match }) {
    let data = find(match.params.id);
    return (

        <Element className="pack-page page0" id={data.id} 
            style={{ backgroundColor: '#ffffff' }}
          >
            <QueueAnim key='queueAnim' type={['right','alpha']} className="intro" interval={[200, 700]} delay={800}
                duration={[800, 500]}
                playScale={0.1}>
                <div key='a'>Olá!</div>
                <div key='b'>Sou Alexandre Almeida,</div>
                <div key='c'>Senior Product Designer na <a href="http://ogilvy.com.br">Ogilvy & Mather Brasil</a>,</div>
                <div key='d'></div>
                <div key='e'>São Paulo.</div>
            </QueueAnim>
        </Element>
    );
  }
  class chamaModal extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      abreModal: false, 
      expandido: []
    };
  }
  expandido(){
    this.setState({ expandido: 'sou eu' });
  }

  render() {
    let { location } = this.props;

    let isModal = !!(
      location.state &&
      location.state.modal &&
      this.previousLocation !== location
    ); // not initial render

    return (
      <div>
        <Switch location={isModal ? this.previousLocation : location}>
          <Route exact path="/"/>
          <Route path="/gallery" />
          <Route path="/img/:id" />
        </Switch>
        {isModal ? <Route path="/img/:id" component={Modal} /> : null}
      </div>
    );
  }

}

function Modal({ match, history }) {

  let data = find(match.params.id, 10);

  if (!data) return null;

  let back = e => {
    e.stopPropagation();
    history.goBack();
  };

  return (
    <div
      onClick={back}
      style={{
        position: "absolute",
        top: 0,
        left: 0,
        bottom: 0,
        right: 0,
        background: "rgba(0, 0, 0, 0.15)"
      }}
    >
      <div
        className="modal"
        style={{
          position: "absolute",
          background: "#fff",
          top: 25,
          left: "10%",
          right: "10%",
          padding: 15,
          border: "2px solid #444"
        }}
      >
        <h1>{data.h2}</h1>
        <button type="button" onClick={back}>
          Close
        </button>
      </div>
    </div>
  );
}

  function Conteudo({ match }) {
    let data = find(match.params.id);



    return (

      <span>
      <ScrollOverPack id={ data.hash + data.id }   className={ data.classe + data.id  } style={{ backgroundColor: data.bg , 'z-index': data.zindex }}
            playScale={0.1} >
        <TweenOne className={"tween-one conteudo-colapsado"} key="0" 
              animation={{  delay: 200, duration: 0, translateY: 0, opacity: 1 }}
              style={{ transform: 'translateY(0px)', opacity: 0 }} >          
                 <h2>{data.h2}</h2>
                 <div>{data.descricao}</div>
                 {console.log(data.expanded[0].titulo)}

                          <Link className="expand" to={data.to}
                            // onFocus={this.onFocus}
                            // onBlur={this.onBlur}
                          >
                            Example4
