import React from 'react';
import PropTypes from 'prop-types';
import Dialog from '@material-ui/core/Dialog';
import Button from '@material-ui/core/Button';
import Tween from 'rc-tween-one';
import Slide from '@material-ui/core/Slide';
import content from './SectionsContent';

const find = id => content.find(p => p.id === id);
const styles = {
    appBar: {
      position: 'relative',
    },
    flex: {
      flex: 1,
    },
  };
function DialogTransition(props) {
    return <Slide direction="down" {...props}/>;
}



function Twn({n}) {
    const styles = {
        opacity: 0,
        transform: 'translateY(0)',
        backgroundColor: '#fff',
        top: '0vh'
    }
    const animation = {
        from: {
            delay: 1000,
            duration: 600,
            opacity: 0,
            x: 0,
            top: 0,
            ease: "linear",
            backgroundColor: n.cor2
        },
        to: {
            delay: 1000,
            duration: 300,
            opacity: 1,
            x: 0,
            y: '0',
            top: 0,
            ease: "easeOutCirc",
            backgroundColor: '#fff'
        }

    };
    return (
        <div>
            <Tween
                key='tweenOne2'
                reverseDelay={4000}
                component={'section'}
                animation={[
                {
                    ...animation.from
                }, {
                    ...animation.to
                }
            ]}
                style={styles}
                className={"tween-one cabecalho"}>
                <Botao
                   />
                <h1 style={{
                    color: n.cor2
                }}>{n.letra}</h1>
                <label>{n.label}</label>
                <h2>{n.h2}</h2>
                <div className='descricao'>{n.descricao}</div>
            </Tween>

        </div>
    )
}

class Botao extends React.Component {
    constructor({data, props}) {
        super({data, props});
        this.state = {
            open: false,
            buttonEnterReverse: false,
            buttonCloseReverse: false
        };
        // this.n = find(data.params.n);
    }


      handleClickOpen = () => {
        this.setState({ open: true });
      };

      handleClose = () => {
        this.setState({ open: false });
      };
    render() {
        const { classes } = this.props;
        return (
            <div>
            <Button
                className="ver_mais"
                // to={"anchor" + this.n.id}
                onClick={this.handleClickOpen}
                style={{
                position: 'fixed',
                height: '4em',
                bottom: '10vh',
                left: 0,
                zIndex: 999,
                cursor: 'pointer'
            }}>
                 {'ver projeto' + this.n.id}
            </Button>
<Dialog
                fullScreen
                id={'anchor' + this.n.id}
                elevation={8}
                open={this.state.open}
            //     transitionDuration={{
            //     enter: 0,
            //     exit: 0
            // }}
                TransitionComponent={DialogTransition}
            //     style={{
            //     backgroundColor: '#ffffff',
            //     zIndex: 99999,
            //     height: '100%',
            //     position: 'absolute'
            // }}
            >
                <div className="pack-page">
                    {Twn({n: this.n})}</div>
                {/* <Button
                            style={{
                            backgroundColor: 'transparent',
                            top:0,
                            position: "fixed",
                            zIndex: 9
                        }}
                            // onClick={this.handleClose}
                            >
                                <CloseIcon
                                    style={{
                                    width: 'calc((100vw+100vh)/2/12)',
                                    height: 'calc((100vw+100vh)/2/12)'
                                }}/>
                        </Button> */}

            </Dialog>
            </div>
        )
    }
}
Botao.propTypes = {
    classes: PropTypes.object.isRequired,
  };
  export default Twn ;
