import ScrollAnim from 'rc-scroll-anim';
import Scroll from 'react-scroll';

// var Scroll = require('react-scroll'); import ReactDOM from 'react-dom';
import React from 'react';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import Slide from '@material-ui/core/Slide';
import Zoom from '@material-ui/core/Zoom';
import Fade from '@material-ui/core/Fade';
import {withStyles} from '@material-ui/core/styles';
import QueueAnim from 'rc-queue-anim';
import Tween from 'rc-tween-one';
import TweenOne from 'rc-tween-one';
import Animate from 'rc-animate';
import content from './SectionsContent';
import {Paper} from '@material-ui/core';
import { relative } from 'path';
const Link = ScrollAnim.Link;
const Element = ScrollAnim.Element;
const ScrollOverPack = ScrollAnim.OverPack;
const EventListener = ScrollAnim.Event;

var scroll = Scroll.animateScroll;
var scrollSpy = Scroll.scrollSpy;

function find(id) {
    return content.find(p => p.id === id);
}
const styles = {
    appBar: {
        position: 'relative'
    },
    flex: {
        flex: 1
    }
};
function Transition(props) {
    return <Slide direction="down" {...props}/>;
}

function Sections() {
    return (
        <span >
            <Home/>
        </span>
    )
}
class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {

            paused: true,
            show: true,
            open: false,
            reverse: false,
            buttonEnterReverse: false,
            buttonCloseReverse: false,
            items: [
                {
                    key: 1
                }, {
                    key: 2
                }, {
                    key: 3
                }, {
                    key: 4
                }, {
                    key: 5
                }, {
                    key: 6
                }
            ]
        };
    }
    onPlay = () => {
        this.setState({paused: false, reverse: false, buttonEnterReverse: true, buttonCloseReverse: false});
        setTimeout(() => {
            this.handleClickOpen()
            this.setState({open: true});
        }, 3000);
    }

    handleClickOpen = () => {
        this.setState({
            items: [
                {
                    key: 1
                }, {
                    key: 2
                }, {
                    key: 3
                }, {
                    key: 4
                }, {
                    key: 5
                }, {
                    key: 6
                }
            ]
        })
        setTimeout(() => {
            ScrollAnim
                .scrollScreen
                .unMount();
        }, 1000);
    };
    // componentDidMount() {     setTimeout(() => {         this.setState({show:
    // true});     }, 100);     window.addEventListener('resize',
    // this.handleWindowSizeChange.bind(this));
    // window.removeEventListener('resize', this.handleWindowSizeChange.bind(this));
    // } handleWindowSizeChange = () => {     ScrollAnim         .scrollScreen
    //   .init({loop: false, duration: 1200, ease: "easeInOutQuart",
    // scrollInterval: 800}); };
    handleClose = () => {
        this.setState({items: []});
        this.setState({paused: false, reverse: true, buttonCloseReverse: true, show: true});
        setTimeout(() => {
            this.setState({ buttonEnterReverse: false, open: false, show: false, paused: true});
            ScrollAnim
                .scrollScreen
                .init({loop: false, duration: 1200, ease: "easeInOutQuart", scrollInterval: 800});
        }, 2400);
    };
    render() {
        const classes = this.props;
        return (
            <div>
                <Link className="nav-list logo" onClick={this.handleClose}>
                    Alê Almeida
                </Link>
                <Tween
                            reverse={this.state.buttonEnterReverse}
                            animation={[{
                                delay: 0,
                                duration: 1600,
                                x:0,
                                opacity: 1
                            }
                        ]}
                            style={{
                            transform: 'translateX(-100px)',
                            opacity: 0,
                            height: '100%',
                            position: fixed,
                            zIndex: '9999',
                            top: '50%'
                        }}>
                {content.map(i => (
                    <ScrollOverPack
                        id={i.hash + i.id}
                        className={i.classe + i.id}
                        style={{
                        backgroundColor: i.bg,
                        zIndex: i.zindex
                    }}
                        playScale={0.1}>

                        <TweenOne
                            show={this.state.show}
                            className={"tween-one"}
                            key="0"
                            animation={{
                            delay: 200,
                            duration: 0,
                            translateY: 0,
                            opacity: 1
                        }}
                            style={{
                            transform: 'translateY(0px)',
                            opacity: 0
                        }}>
                            <h2>{i.h2}</h2>
                            <div>{i.descricao}</div>
                        </TweenOne>
                        <Tween
                            className="tween"
                            reverse={this.state.buttonEnterReverse}
                            animation={[{
                                delay: 600,
                                duration: 600,
                                x: 0,
                                opacity: 1
                            }
                        ]}
                            style={{
                            transform: 'translateX(-10px)',
                            opacity: 0,
                            position: 'fixed',
                            zIndex: '9999',
                            justifyContent: 'flex-start',
                            top: '50%'
                        }}>

                            <Button
                                style={{
                                width: 'auto',
                                height: 'auto'
                            }}
                                onClick={this.onPlay}>
                                Ver projeto
                            </Button>
                        </Tween>
                        <Dialog
                            fullScreen
                            id={'anchor' + i.id}
                            className="pack-page"
                            elevation={8}
                            scroll={this.state.scroll}
                            open={this.state.open}
                            onClose={this.handleClose}
                            transitionDuration={{
                            enter: 0,
                            exit: 0
                        }}
                            TransitionComponent={Transition}
                            style={{
                            backgroundColor: i.bg,
                            zIndex: 10,
                            position: relative,
                            height: '100%'
                        }}>
                            <Button
                                to={"anchor" + i.id}
                                style={{
                                backgroundColor: 'transparent'
                            }}
                                color="inherit"
                                onClick={this.handleClose}>
                                <Tween
                                    reverseDelay={400}
                                    reverse={this.state.buttonCloseReverse}
                                    animation={[{
                                        delay: 2800,
                                        duration: 500,
                                        rotate: 180,
                                        opacity: 1
                                    }
                                ]}
                                    style={{
                                    transform: 'rotate(0deg)',
                                    transformOrigin: '50%, 50%',
                                    width: '40px',
                                    height: '40px',
                                    position: 'fixed',
                                    opacity: 0,
                                    zIndex: '9999',
                                    textAlign: "center",
                                    top: '2em',
                                    right: '2em'
                                }}>
                                    <CloseIcon
                                        style={{
                                        width: '40px',
                                        height: '40px'
                                    }}/>
                                </Tween>
                            </Button>
                            <TweenOne
                                id={'anchor' + i.id}
                                show={this.state.show}
                                className={"tween-one"}
                                reverse={this.state.reverse}
                                reverseDelay={400}
                                key="0"
                                animation={{
                                duration: 100,
                                translateY: 0,
                                bezier: 2,
                                opacity: 1
                            }}
                                style={{
                                transform: 'translateY(0px)',
                                opacity: 1,
                                zIndex: 1
                            }}>
                                <h2>{i.h2}</h2>
                                <div>{i.descricao}</div>
                                <QueueAnim
                                    type={['right', 'right']}
                                    interval={[300, 800]}
                                    delay={300}
                                    duration={[1000, 1000]}
                                    ease={['easeOutBack', 'easeInOutCirc']}
                                    leaveReverse>
                                    {this
                                        .state
                                        .items
                                        .map((item) => <div key={item.key}>
                                            <section>
                                                <p>A scrollScreen demo</p>
                                                {/* {item.children} */}
                                            </section>
                                        </div>)}
                                </QueueAnim>

                            </TweenOne>

                        </Dialog>

                    </ScrollOverPack>
                ))}
                </Tween>
<Tween
                        paused={this.state.paused}
                        reverse={this.state.reverse}
                        reverseDelay={50}
                        animation={{
                        x: '0vw',
                        width: '400vw',
                        left: '100%',
                        delay: 0,
                        duration: 3000,
                        ease: 'easeInOutExpo',
                        opacity: 1
                    }}
                        style={{
                        transform: 'translateX("0vw")',
                        backgroundColor: '#222',
                        height: '100vh',
                        top: '0',
                        left: '-160%',
                        position: "fixed",
                        width: '150vw',
                        zIndex: 9999999999999,
                        opacity: 1
                    }}
                        key="t"/>
            </div>
        );
    }
}
function Intro({match}) {

    return (

        <Element
            className="pack-page page0"
            id='page0'
            style={{
            backgroundColor: '#ffffff',
            zIndex: 1500
        }}>
            <QueueAnim
                key='queueAnim'
                type={['right', 'alpha']}
                className="intro"
                interval={[200, 700]}
                delay={800}
                duration={[800, 500]}
                playScale={0.1}>
                <div key='a'>Olá!</div>
                <div key='b'>Sou Alexandre Almeida,</div>
                <div key='c'>Senior Product Designer na
                    <a href="http://ogilvy.com.br">Ogilvy & Mather Brasil</a>,
                </div>
                <div key='d'></div>
                <div key='e'>São Paulo.</div>

            </QueueAnim>
        </Element>
    );
}
export default Sections;
