import React from 'react';
import TweenOne from 'rc-tween-one';

function DialogTransition({n}) {
    const styles = {
        width: '100%',
        height: '100vh',
        position: 'absolute',
        zIndex: '0',
        backgroundColor: n.cor2,
        opacity: 1,
        top: 0,
        left: 0,
        borderRadius: '30%',
        transform: 'translateX(-30%) translateY(-40%) scale(0, 0.5)'
    }
    const animation = {
        from: 
            {
                delay: 0,
                        duration: 0,
                        opacity: 1,
                        top: 0,
                        left: 0,
                        ease: "easeInQuad",
                        backgroundColor: n.cor2,
                        type: 'from',
                        scaleX: 0, scaleY: 0.5
            }
        ,
        to: 
            {
                delay: 100,
                        duration: 2000,
                        opacity: 1,
                        top: '-10vh',
                        ease: "easeInOutBack",
                        backgroundColor: n.cor2,
                        type: 'to',
                        scaleX: 3, scaleY: .9,
                        x: 0,
                        y: 0
            }

    };
    return (
        <TweenOne
        key="tweenOne"
                animation={
                [animation.from, animation.to]
            }
                style={styles}/>
    )
}

export default DialogTransition;
