import React from 'react';
import Tween from 'rc-tween-one';
import ScrollAnim from 'rc-scroll-anim';
import Dialog from '@material-ui/core/Dialog';
import Button from '@material-ui/core/Button';
import Slide from '@material-ui/core/Slide';
import CloseIcon from '@material-ui/icons/Close';
import content from './SectionsContent';
import {relative} from 'path';

const onPlay = ({n}) => {
    console.log({n})
};
const find = id => content.find(p => p.id === id);
function Transition(props) {
    return <Slide direction="down" {...props}/>;
}
function Twn({n}) {
    const styles = {
        opacity: 0,
        transform: 'translateY(0)',
        backgroundColor: '#fff',
        top: '0vh'
    }
    const animation = {
        from: {
            delay: 1000,
            duration: 600,
            opacity: 0,
            x: 0,
            top: 0,
            ease: "linear",
            backgroundColor: n.cor2
        },
        to: {
            delay: 1000,
            duration: 300,
            opacity: 1,
            x: 0,
            y: '0',
            top: 0,
            ease: "easeOutCirc",
            backgroundColor: '#fff'
        }

    };
    return (
        <Tween
            key='tweenOne2'
            reverseDelay={4000}
            component={'section'}
            animation={[
            {
                ...animation.from
            }, {
                ...animation.to
            }
        ]}
            style={styles}
            className={"tween-one cabecalho"}>
            <h1 style={{
                color: n.cor2
            }}>{n.letra}</h1>
            <label>{n.label}</label>
            <h2>{n.h2}</h2>
            <div className='descricao'>{n.descricao}</div>
            <Button to={"anchor" + this.n.id} onClick={onPlay} style={{position: 'relative', height: '4em',  cursor: 'pointer'}}>
                <i class="material-icons ver_mais">
                    trending_flat
                </i>
            </Button>
        </Tween>
    )
}
class Botao extends React.Component {
    constructor({data, props}) {
        super({data, props});
        this.state = {
            paused: true,
            show: true,
            open: false,
            reverse: false,
            buttonEnterReverse: false,
            buttonCloseReverse: false
        };
        this.n = find(data.params.n);
    }

    onPlay = () => {
        this.setState({paused: false, reverse: false, buttonEnterReverse: true, buttonCloseReverse: false});
        setTimeout(() => {
            this.handleClickOpen()
            this.setState({open: true});
        }, 3400);
    }

    handleClickOpen = () => {
        setTimeout(() => {
            ScrollAnim
                .scrollScreen
                .unMount();
        }, 1000);
    }
    render() {
        return (
            <div>

            <Dialog
                            fullScreen
                            id={'anchor' + this.n.id}
                            className="pack-page"
                            elevation={8}
                            scroll={this.state.scroll}
                            open={this.state.open}
                            onClose={this.handleClose}
                            transitionDuration={{
                            enter: 0,
                            exit: 0
                        }}
                            TransitionComponent={Transition}
                            style={{
                            backgroundColor: '#ffffff',
                            zIndex: 10,
                            position: relative,
                            height: '100%'
                        }}>
                        {
        console.log(this.n)}
                        {Twn({n : this.n})}
                            <Button
                                style={{
                                backgroundColor: 'transparent',
                                top:0,
                                position: "fixed",
                                zIndex: 9
                            }}
                                // onClick={this.handleClose}
                                >
                                    <CloseIcon
                                        style={{
                                        width: 'calc((100vw+100vh)/2/12)',
                                        height: 'calc((100vw+100vh)/2/12)'
                                    }}/>
                            </Button>

                        </Dialog>
                        </div>
        )
    }
}

export default Twn;
