import React from 'react';
import PropTypes from 'prop-types';
import Dialog from '@material-ui/core/Dialog';
import Button from '@material-ui/core/Button';
import ScrollAnim from 'rc-scroll-anim';
import content from './SectionsContent';
import iniciaScroll from './Scroll';
import Twn from './Twn';
import Transition from './Transition';
import Slide from '@material-ui/core/Slide';
const ScrollOverPack = ScrollAnim.OverPack;


const find = id => content.find(p => p.id === id);


const styles = {
    appBar: {
      position: 'relative',
    },
    flex: {
      flex: 1,
    },
  };
function DialogTransition(props) {
    return <Slide direction="down" {...props}/>;
}


const OverPack = ({data}) => {
    let n = find(data.params.id);

    return (
        <ScrollOverPack
            always={true}
            appear={true}
                componentProps={{ tweenOne2:  {reverse :true }, tweenOne:  {reverse :true } }}
            className={n.classe}
            id={n.hash}
            playScale={0.5}>
            <Botao
                data={{
                params: {
                    n: n.id
                }
            }}/>
            {Twn({n: n})}
            {Transition({n: n})}
        </ScrollOverPack>
    )
}
class Home extends React.Component {
    componentDidMount() {
        iniciaScroll();
    }
    render() {

        return (
            content.map((i) => (<OverPack
            data={{
            params: {
                id: i.id
            }
        }}></OverPack>)));
    }
}

class Botao extends React.Component {
    constructor({data, props}) {
        super({data, props});
        this.state = {
            paused: true,
            show: true,
            open: false,
            reverse: false,
            buttonEnterReverse: false,
            buttonCloseReverse: false
        };
        this.n = find(data.params.n);
    }


      handleClickOpen = () => {
        this.setState({ open: true });
      };

      handleClose = () => {
        this.setState({ open: false });
      };
    render() {
        const { classes } = this.props;
        return (
            <div>
            <Button
                className="ver_mais"
                // to={"anchor" + this.n.id}
                onClick={this.handleClickOpen}
                style={{
                position: 'fixed',
                height: '4em',
                bottom: '10vh',
                left: 0,
                zIndex: 999,
                cursor: 'pointer'
            }}>
                 {'ver projeto' + this.n.id}
            </Button>
<Dialog
                fullScreen
                id={'anchor' + this.n.id}
                elevation={8}
                open={this.state.open}
            //     transitionDuration={{
            //     enter: 0,
            //     exit: 0
            // }}
                TransitionComponent={DialogTransition}
            //     style={{
            //     backgroundColor: '#ffffff',
            //     zIndex: 99999,
            //     height: '100%',
            //     position: 'absolute'
            // }}
            >
                <div className="pack-page">
                    {Twn({n: this.n})}</div>
                {/* <Button
                            style={{
                            backgroundColor: 'transparent',
                            top:0,
                            position: "fixed",
                            zIndex: 9
                        }}
                            // onClick={this.handleClose}
                            >
                                <CloseIcon
                                    style={{
                                    width: 'calc((100vw+100vh)/2/12)',
                                    height: 'calc((100vw+100vh)/2/12)'
                                }}/>
                        </Button> */}

            </Dialog>
            </div>
        )
    }
}
Botao.propTypes = {
    classes: PropTypes.object.isRequired,
  };
export default Home;
