import React from 'react';
import Tween from 'rc-tween-one';
import ScrollAnim from 'rc-scroll-anim';
import Dialog from '@material-ui/core/Dialog';
import Button from '@material-ui/core/Button';
import Slide from '@material-ui/core/Slide';
import CloseIcon from '@material-ui/icons/Close';
import content from './SectionsContent';
import {relative} from 'path';

const find = id => content.find(p => p.id === id);

function Transition(props) {
    return <Slide direction="down" {...props}/>;
}

function Twn({n}) {
    const styles = {
        opacity: 0,
        transform: 'translateY(0)',
        backgroundColor: '#fff',
        top: '0vh'
    }
    function setState(state) {
        this.state = Object.assign({}, this.state, state);
    }
    const animation = {
        from: {
            delay: 1000,
            duration: 600,
            opacity: 0,
            x: 0,
            top: 0,
            ease: "linear",
            backgroundColor: n.cor2
        },
        to: {
            delay: 1000,
            duration: 300,
            opacity: 1,
            x: 0,
            y: '0',
            top: 0,
            ease: "easeOutCirc",
            backgroundColor: '#fff'
        }

    };
    const onPlay = (state) => {
        setState((state) => ({open: true}));
        console.log({n})
    };
    return (
        <div>
            <Tween
                key='tweenOne2'
                reverseDelay={4000}
                component={'section'}
                animation={[
                {
                    ...animation.from
                }, {
                    ...animation.to
                }
            ]}
                style={styles}
                className={"tween-one cabecalho"}>
                <h1 style={{
                    color: n.cor2
                }}>{n.letra}</h1>
                <label>{n.label}</label>
                <h2>{n.h2}</h2>
                <div className='descricao'>{n.descricao}</div>
            </Tween>
            <Botao
                data={{
                params: {
                    n: n.id
                }
            }}/>

        </div>
    )
}
class Botao extends React.Component {
    constructor({data, props}) {
        super({data, props});
        this.state = {
            paused: true,
            show: true,
            open: false,
            reverse: false,
            buttonEnterReverse: false,
            buttonCloseReverse: false
        };
        this.n = find(data.params.n);
    }

    onPlay = () => {
        this.setState({paused: false, reverse: false, buttonEnterReverse: true, buttonCloseReverse: false});
        setTimeout(() => {
            this.handleClickOpen();
            this.setState({open: true});
        }, 5400);
    }

    handleClickOpen = () => {
        setTimeout(() => {
            ScrollAnim
                .scrollScreen
                .unMount();
        }, 1000);
    }
    render() {
        return (
            <div>
<Dialog
                fullScreen
                id={'anchor' + this.n.id}
                elevation={8}
                open={this.state.open}
                transitionDuration={{
                enter: 0,
                exit: 0
            }}
                TransitionComponent={Transition}
                style={{
                backgroundColor: '#ffffff',
                zIndex: 99999,
                height: '100%',
                position: 'absolute'
            }}>
                <div className="pack-page">
                    {Twn({n: this.n})}</div>
                {/* <Button
                            style={{
                            backgroundColor: 'transparent',
                            top:0,
                            position: "fixed",
                            zIndex: 9
                        }}
                            // onClick={this.handleClose}
                            >
                                <CloseIcon
                                    style={{
                                    width: 'calc((100vw+100vh)/2/12)',
                                    height: 'calc((100vw+100vh)/2/12)'
                                }}/>
                        </Button> */}

            </Dialog>
                <Button
                    className="ver_mais"
                    to={"anchor" + this.n.id}
                    onClick={this.onPlay}
                    style={{
                    position: 'fixed',
                    height: '4em',
                    bottom: 0,
                    zIndex: 999,
                    cursor: 'pointer'
                }}>
                     {'ver projeto' + this.n.id}
                </Button>
            </div>
        )
    }
}

export default Twn;
