import ScrollAnim from 'rc-scroll-anim';
// import ReactDOM from 'react-dom';
import React from 'react';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import Slide from '@material-ui/core/Slide';
import Zoom from '@material-ui/core/Zoom';
import Fade from '@material-ui/core/Fade';
import {withStyles} from '@material-ui/core/styles';
import QueueAnim from 'rc-queue-anim';
import TweenOne from 'rc-tween-one';
import Animate from 'rc-animate';
import content from './SectionsContent';
import Paper from '@material-ui/core/Paper';
import PropTypes from 'prop-types';
const Link = ScrollAnim.Link;
const Element = ScrollAnim.Element;
const ScrollOverPack = ScrollAnim.OverPack;

function find(id) {
    return content.find(p => p.id === id);
}
const styles = theme => ({
    root: {
        ...theme
            .mixins
            .gutters(),
        paddingTop: '30vh',
        paddingBottom: theme.spacing.unit * 2,
        backgroundColor: '#676767'
    }
});

function Transition(props) {
    return (<Fade in={true} { ...props }/>)
}

function Sections() {
    return (
        <span >
            <Home/>
        </span>
    )
}
class Home extends React.Component {
    state = {
        open: false
    };
    handleClickOpen = () => {
        this.setState({open: true});
        ScrollAnim
            .scrollScreen
            .unMount();
    };
    handleClose = () => {
        this.setState({open: false});
        ScrollAnim
            .scrollScreen
            .init({loop: false, duration: 1200, ease: "easeInOutQuart", scrollInterval: 800});
    };
    render() {

  const { classes } = props;
        return (
            <div>
                <Link className="nav-list logo" offsetTop={100} onClick={this.handleClose}>
                    Alê Almeida
                </Link>
                {content.map(i => (

                    <ScrollOverPack
                        id={i.hash + i.id}
                        className={i.classe + i.id}
                        style={{
                        backgroundColor: i.bg,
                        zIndex: i.zindex
                    }}
                        playScale={0.1}>
                        <TweenOne
                            className={"tween-one"}
                            key="0"
                            animation={{
                            delay: 200,
                            duration: 0,
                            translateY: 0,
                            opacity: 1
                        }}
                            style={{
                            transform: 'translateY(0px)',
                            opacity: 0
                        }}>
                            <h2>{i.h2}</h2>
                            <div>{i.descricao}</div>
                            <Button
                                style={{
                                position: 'fixed',
                                zIndex: '9999',
                                top: '1em',
                                right: '1em'
                            }}
                                onClick={this.handleClickOpen}>
                                Open full - screen dialog
                            </Button>
                        </TweenOne>
                        <Dialog
                            fullScreen
                            scroll={this.state.scroll}
                            open={this.state.open}
                            onClose={this.handleClose}
                            transitionDuration={0}
                            transitionComponent={Transition}
                            style={{
                            backgroundColor: '#dddddd'
                        }}>
                            <IconButton
                                style={{
                                position: 'fixed',
                                zIndex: '9999',
                                top: '1em',
                                right: '1em',
                                backgroundColor: '#f0f0f0'
                            }}
                                color="inherit"
                                onClick={this.handleClose}>
                                <CloseIcon/>
                            </IconButton>
                            <TweenOne
                                className={"tween-one"}
                                key="0"
                                animation={{
                                delay: 0,
                                duration: 0,
                                translateY: 0
                            }}
                                style={{
                                transform: 'translateY(0px)'
                            }}>
                                <h2>{i.h2}</h2>
                                <div>{i.descricao}</div>
                                <QueueAnim>
                                    <div className="page-description" key="a">
                                        <p>A scrollScreen demo</p>
                                    </div>
                                    <div className="page-description" key="b">
                                        <p>A scrollScreen demo</p>
                                    </div>
                                    <div className="page-description" key="c">
                                        <p>A scrollScreen demo</p>
                                    </div>
                                    <div className="page-description" key="d">
                                        <p>A scrollScreen demo</p>
                                    </div>
                                </QueueAnim>
                            </TweenOne>
                            <Paper className={classes.root} elevation={1}>jajaja</Paper>
                        </Dialog>
                    </ScrollOverPack>
                ))}
            </div>
        );
    }
}
function Intro({match}) {

    return (

        <Element
            className="pack-page page0"
            id='page0'
            style={{
            backgroundColor: '#ffffff',
            zIndex: 1500
        }}>
            <QueueAnim
                key='queueAnim'
                type={['right', 'alpha']}
                className="intro"
                interval={[200, 700]}
                delay={800}
                duration={[800, 500]}
                playScale={0.1}>
                <div key='a'>Olá!</div>
                <div key='b'>Sou Alexandre Almeida,</div>
                <div key='c'>Senior Product Designer na
                    <a href="http://ogilvy.com.br">Ogilvy & Mather Brasil</a>,
                </div>
                <div key='d'></div>
                <div key='e'>São Paulo.</div>

            </QueueAnim>
        </Element>
    );
}

Sections.propTypes = {
    classes: PropTypes.object.isRequired
};

export default withStyles(styles)(Sections);
// export default Sections;
