import ScrollAnim from 'rc-scroll-anim';
import ReactDOM from 'react-dom';
import React from 'react';
import {
	HashRouter as Router,
	Route,
	Switch
} from 'react-router-dom';
import QueueAnim from 'rc-queue-anim';
// import TweenOne from 'rc-tween-one';
import AriaModal from '../../react-aria-modal';
// import FocusTrap from 'focus-trap-react';
import content from './SectionsContent';
import {
	Contexto,
	datas
} from './SectionsContexto';
import Botao from './SectionsBotoes';
const FocusTrap = require('focus-trap-react');
// const AriaModal = require('../../react-aria-modal');
//ScrollAnim.scrollScreen.unMount({ });
const Link = ScrollAnim.Link;
const Element = ScrollAnim.Element;
const ScrollOverPack = ScrollAnim.OverPack;
const EventListener = ScrollAnim.Event;
const ScrollParallax = ScrollAnim.Parallax;

function find(id) {
	return content.find(p => p.id === id);
}

function Sections() {
	// this.state = {
	//   modalActive: false
	// };
	return ( < span > < Home / > < /span>)
	}


	class Home extends React.Component {

			constructor(props) {
				super(props);
				this.state = {
					modalActive: false,
				};
				this.activateModal = this.activateModal.bind(this);
				this.deactivateModal = this.deactivateModal.bind(this);
				this.getApplicationNode = this.getApplicationNode.bind(this);
				this.activateInnerFocusTrap = this.activateInnerFocusTrap.bind(this);
				this.deactivateInnerFocusTrap = this.deactivateInnerFocusTrap.bind(this);
			}
			activateModal = () => {
				this.setState({
					modalActive: true
				});
			};
			deactivateModal = () => {
				this.setState({
					modalActive: false
				});
			};
			activateInnerFocusTrap = () => {
				this.setState({
					innerFocusTrapActive: true
				});
			};
			deactivateInnerFocusTrap = () => {
				this.setState({
					innerFocusTrapActive: false
				});
			};
			getApplicationNode = () => {
				return document.getElementById('application');
			};
			render() {
					return ( < div > < Intro match = {
							{
								params: {
									id: 0
								},
								titulo: 'Intro'
							}
						}
						/> {
						content.map(i => ( < ScrollOverPack id = {
									i.hash + i.id
								}
								className = {
									i.classe + i.id
								}
								style = {
									{
										backgroundColor: i.bg,
										'zIndex': i.zindex
									}
								}
								playScale = {
									0.1
								} > < /ScrollOverPack > ))
							} < /div>);
						}
					}

					function Intro({
						match
					}) {
						let data = find(match.params.id);
						return ( < Element className = "pack-page page0"
							id = 'page0'
							style = {
								{
									backgroundColor: '#ffffff'
								}
							} > < QueueAnim key = 'queueAnim'
							type = {
								['right', 'alpha']
							}
							className = "intro"
							interval = {
								[200, 700]
							}
							delay = {
								800
							}
							duration = {
								[800, 500]
							}
							playScale = {
								0.1
							} > < div key = 'a' > Olá! < /div> <
							div key = 'b' > Sou Alexandre Almeida, < /div> <
							div key = 'c' > Senior Product Designer na < a href = "http://ogilvy.com.br" > Ogilvy & Mather Brasil < /a>,</div > < div key = 'd' > < /div> <
							div key = 'e' > São Paulo. < /div> < /QueueAnim > < /
							Element > );
					}


					export default Sections; {
						/* ReactDOM.render([ < ScrollParallax animation = {
											{
												y: 10
											}
										} > < /ScrollParallax >  ], document.getElementById('modal')); */
					}
