import React from 'react';
import Tween from 'rc-tween-one';
import Button from '@material-ui/core/Button';import ScrollAnim from 'rc-scroll-anim';


function Twn({n}) {
    const styles = {
        opacity: 0,
        transform: 'translateY(0)',
        backgroundColor: '#fff',
        top: '0vh'
    }
    const animation = {
        from: {
            delay: 1000,
            duration: 600,
            opacity: 0,
            x: 0,
            top: 0,
            ease: "linear",
            backgroundColor: n.cor2
        },
        to: {
            delay: 1000,
            duration: 300,
            opacity: 1,
            x: 0,
            y: '0',
            top: 0,
            ease: "easeOutCirc",
            backgroundColor: '#fff'
        }

    };
    return (
        <Tween
            key='tweenOne2'
            reverseDelay={4000}
            component={'section'}
            animation={[
            {
                ...animation.from
            }, {
                ...animation.to
            }
        ]}
            style={styles}
            className={"tween-one cabecalho"}>
            <h1 style={{
                color: n.cor2
            }}>{n.letra}</h1>
            <label>{n.label}</label>
            <h2>{n.h2}</h2>
            <div className='descricao'>{n.descricao}</div>
            <Botao />
        </Tween>
    )
}
class Botao extends React.Component {
    constructor(props) {
        super(props);
        this.state = {

            paused: true,
            show: true,
            open: false,
            reverse: false,
            buttonEnterReverse: false,
            buttonCloseReverse: false
        };
    }

    onPlay = () => {
        this.setState({
            open: true, paused: false, reverse: false, buttonEnterReverse: true, buttonCloseReverse: false});
        setTimeout(() => {
            this.handleClickOpen()
            this.setState({open: true});
        }, 3400);
    }

    handleClickOpen = () => {
        setTimeout(() => {
            ScrollAnim
                .scrollScreen
                .unMount();
        }, 1000);
    }
    render() {
        return (
            <Button onClick={this.onPlay} style={{cursor: 'pointer'}}>
                <i class="material-icons ver_mais">
                    trending_flat
                </i>
            </Button>
        )
    }
}

export default Twn;
