import ScrollAnim from 'rc-scroll-anim';
import React from 'react';
import { HashRouter as Router, Route} from 'react-router-dom';

import routes from './MenuContent';
const Link = ScrollAnim.Link;
const EventListener = ScrollAnim.Event;

function Menu() {
  return (
    <Links />
  )
}
class Links extends React.Component {
  onFocus = (e) => {
    this.dom = e.target;


    // this.barAnimate();
    var idOn = document.getElementById(e.to)

    console.log(idOn)// if(e.target.index)

        idOn.className =  "pack-page " + e.to + " active"
  }
  onBlur = (e) => {
    var idOn = document.getElementById(e.to);
    idOn.className =  "pack-page " + e.to;
  }
  render() {
    return (
      <div className="nav">
            <div className="nav-wap">
            <Link className="nav-list" to="page0" showHeightActive={['50%', '10%']}
            onFocus={this.onFocus}
            onBlur={this.onBlur}
          >
            Example
          </Link>
          <Link className="nav-list" to="page1" showHeightActive={['10%', '60%']} toShowHeight
            onFocus={this.onFocus}
            onBlur={this.onBlur}
          >
            Example2
          </Link>
          <Link className="nav-list" to="page2" showHeightActive={['60%', '50%']} toShowHeight
            onFocus={this.onFocus}
            onBlur={this.onBlur}
          >
            Example3
          </Link>
          <Link className="nav-list" to="page3" offsetTop={100}
            onFocus={this.onFocus}
            onBlur={this.onBlur}
          >
            Example4
          </Link>
          <div ref={(c) => { this.bar = c; }} className="nav-bar" />
        </div>
          </div>
    );
  }
}

export default Menu;
